import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './GamesNews.css'; // Import the CSS file

const GamesNews = () => {
    const [news, setNews] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const apiKey = 'bd158c9a65504f5786aa5ca60bf0bc21';
    const games = [
        'League of Legends',
        'Warzone',
        'Valorant',
        'Fortnite'
    ];

    const trustedSources = [
        'Dot Esports',
        'IGN',
        'Dexerto',
        'Polygon',
        'PC Gamer',
        'GameSpot',
        'The Esports Observer',
        'Esports Insider',
        'Esports.net',
        'Upcomer'
    ];

    useEffect(() => {
        const fetchNewsForGames = async () => {
            try {
                const newsPromises = games.map(game =>
                    axios.get('https://newsapi.org/v2/everything', {
                        params: {
                            q: game,
                            language: 'en',
                            apiKey: apiKey,
                            pageSize: 20 // Fetch more articles, adjust this number as needed
                        }
                    })
                );

                const responses = await Promise.all(newsPromises);

                const allArticles = [];

                responses.forEach((response) => {
                    let articles = response.data.articles;

                    // Add a check to avoid empty articles
                    if (articles.length > 0) {
                        articles.forEach(article => {
                            // Avoid duplicate articles by checking if it's already added
                            if (!allArticles.some(a => a.title === article.title)) {
                                allArticles.push(article);
                            }
                        });
                    }
                });

                setNews(allArticles.sort((a, b) => new Date(b.publishedAt) - new Date(a.publishedAt)));
                setLoading(false);
            } catch (err) {
                setError('Failed to fetch news');
                setLoading(false);
            }
        };

        fetchNewsForGames();
    }, []);

    const filterByDate = (articles, daysAgo = 7) => {
        const cutoffDate = new Date();
        cutoffDate.setDate(cutoffDate.getDate() - daysAgo);

        return articles.filter(article => new Date(article.publishedAt) >= cutoffDate);
    };

    const filterBySource = (articles) => {
        return articles.filter(article => trustedSources.includes(article.source.name));
    };

    if (loading) {
        return <p>Loading news...</p>;
    }

    if (error) {
        return <p>{error}</p>;
    }

    return (
        <div className="games-news-container">
            <h2>Latest News</h2>
            <div className="news-grid">
                {news.length > 0 ? (
                    news.map((article, index) => (
                        <div key={index} className="news-card">
                            {article.urlToImage && (
                                <img src={article.urlToImage} alt={article.title} className="news-image" />
                            )}
                            <div className="news-content">
                                <a href={article.url} target="_blank" rel="noopener noreferrer">
                                    <h3 className="news-title">{article.title}</h3>
                                </a>
                                <p className="news-description">{article.description}</p>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>No news available.</p>
                )}
            </div>
        </div>
    );
};

export default GamesNews;
