import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Calendar from 'react-calendar'; // Make sure you have react-calendar installed
import 'react-calendar/dist/Calendar.css'; // Import the CSS for the calendar
import './TournamentCalendar.css'; // Your CSS file for styling
import Navbar from './Navbar';

const TournamentCalendar = () => {
  const [tournaments, setTournaments] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [selectedTournament, setSelectedTournament] = useState(null);

  useEffect(() => {
    const fetchTournaments = async () => {
      try {
        const response = await axios.get('http://localhost:3001/tournaments'); // Ensure this endpoint is correct
        setTournaments(response.data);
        console.log('Fetched Tournaments:', response.data); // Log fetched tournaments
      } catch (error) {
        console.error('Error fetching tournaments:', error);
      }
    };

    fetchTournaments();
  }, []);


  const handleDateChange = (date) => {
    setSelectedDate(date);
    const selectedISODate = date.toISOString(); // Full ISO string
    console.log('Selected Date:', selectedISODate); // Log selected date

    const selectedDateString = selectedISODate.slice(0, 10); // Get YYYY-MM-DD
    console.log('Selected Date String:', selectedDateString); // Log the date string

    const tournament = tournaments.find(t => t.date.slice(0, 10) === selectedDateString);
    console.log('Matching Tournament:', tournament); // Log found tournament

    setSelectedTournament(tournament);
  };



  return (
    <div>
      <Navbar />
      <div className="calendar-container">
        <Calendar onChange={handleDateChange} value={selectedDate} />
        {selectedTournament ? (
          <div className="tournament-details">
            <h2>Tournament Details</h2>
            <p><strong>Name:</strong> {selectedTournament.name}</p>
            <p><strong>Game:</strong> {selectedTournament.game}</p>
            <p><strong>Date:</strong> {selectedTournament.date}</p>
            <p><strong>Time:</strong> {selectedTournament.time}</p>
            <p><strong>Teams:</strong> {selectedTournament.teams.join(', ')}</p>
            <p><strong>Prize Pool:</strong> ${selectedTournament.prizePool}</p>
            <p><strong>Organizer:</strong> {selectedTournament.organizer}</p>
          </div>
        ) : (
          <div>No tournament found for this date.</div>
        )}
      </div>
    </div>
  );
};

export default TournamentCalendar;
