import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import LogoutButton from './LogoutButton';

const Navbar = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState('');

  useEffect(() => {
    // Check if user is logged in when the component mounts
    const checkAuth = async () => {
      try {
        const response = await axios.get('http://localhost:3001/profile', { withCredentials: true });
        setIsAuthenticated(true);
        setUsername(response.data.name);
      } catch {
        setIsAuthenticated(false);
      }
    };

    checkAuth();
  }, []);

  return (
    <nav className='navbar'>
      <Link to='/'>
        <img src="/images/logo.png" alt="Logo" className="logo" />
      </Link>
      {/* Other links and components */}

      <div className="auth-buttons">
        {isAuthenticated ? (
          <div className='auth-button'>
            <Link to="/profile" className='btn username'>{username}</Link>
            <LogoutButton setIsAuthenticated={setIsAuthenticated} setUsername={setUsername} />
          </div>
        ) : (
          <>
            <Link to="/login" className='auth-button'>
              <button className='btn login-btn'>Login</button>
            </Link>
            <Link to="/signup" className='auth-button'>
              <button className='btn signup-btn'>Signup</button>
            </Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
