// src/components/LoginForm.js
import React, { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import './Login.css';
import Navbar from './Navbar';
import { UserContext } from './UserContext'; // Import UserContext

const Login = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const { setUser } = React.useContext(UserContext); // Use UserContext
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3001/login', formData, { withCredentials: true });
      setSuccess(response.data.message);
      setError('');
      // Fetch the user's profile
      const profileResponse = await axios.get('http://localhost:3001/profile', { withCredentials: true });
      setUser(profileResponse.data); // Set user data in context
      navigate('/home'); // Redirect to home or another page
    } catch (err) {
      setError(err.response ? err.response.data.message : "Something went wrong");
      setSuccess('');
    }
  };

  return (
    <div>
      <Navbar />
      <div className='background'>
        <div className="login-container">
          <h2>Login</h2>
          {error && <p className="error">{error}</p>}
          {success && <p className="success">{success}</p>}
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Email:</label>
              <input type="email" name="email" value={formData.email} onChange={handleChange} required />
            </div>
            <div className="form-group">
              <label>Password:</label>
              <input type="password" name="password" value={formData.password} onChange={handleChange} required />
            </div>
            <button type="submit" className="login-button">Login</button>
          </form>
          <p className="signup-redirect">Don't have an account? <Link to="/signup">Sign up</Link></p>
        </div>
      </div>
    </div>
  );
};

export default Login;
