app.get('/api/esports-news', async (req, res) => {
    try {
      const response = await axios.get('https://api.sportdevs.com/news', {
        headers: {
          'Authorization': 'Bearer 0AAh34asVkuAYJcWp8eY4g' // Use your token here
        }
      });
      res.json(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
      res.status(500).send('Server Error');
    }
  });
