import { useState, useEffect } from 'react';
import axios from 'axios';

const EsportsNews = () => {
  const [news, setNews] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Update this URL based on the endpoint you want to access
    axios.get('https://api.sportdevs.com/news', {
      headers: {
        'Authorization': 'Bearer 0AAh34asVkuAYJcWp8eY4g' // Use your token here
      }
    })
    .then(response => {
      setNews(response.data); // Adjust this based on the actual data structure
    })
    .catch(err => {
      setError(err.message);
      console.error('Error fetching data:', err);
    });
  }, []);

  if (error) return <div>Error: {error}</div>;
  if (!news.length) return <div>Loading...</div>;

  return (
    <div>
      {news.map((item, index) => (
        <div key={index}>
          {/* Adjust this based on the structure of your response */}
          <h2>{item.title}</h2>
          <p>{item.description}</p>
        </div>
      ))}
    </div>
  );
};

export default EsportsNews;
