// UpcomingTournaments.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './UpcomingTournaments.css'; // Create a CSS file for styling

const UpcomingTournaments = () => {
  const [tournaments, setTournaments] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchTournaments = async () => {
      try {
        const response = await axios.get('http://localhost:3001/tournaments');
        setTournaments(response.data);
      } catch (error) {
        console.error('Error fetching tournaments:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchTournaments();
  }, []);

  if (loading) {
    return <div>Loading tournaments...</div>;
  }

  return (
    <div className="tournaments-container">
      <h2>Upcoming Tournaments</h2>
      {tournaments.length === 0 ? (
        <div>No tournaments found.</div>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Game</th>
              <th>Date</th>
              <th>Time</th>
              <th>Teams</th>
              <th>Prize Pool</th>
              <th>Organizer</th>
            </tr>
          </thead>
          <tbody>
            {tournaments.map((tournament) => (
              <tr key={tournament._id}>
                <td>{tournament.name}</td>
                <td>{tournament.game}</td>
                <td>{new Date(tournament.date).toLocaleDateString()}</td>
                <td>{tournament.time}</td>
                <td>{tournament.teams.join(', ')}</td>
                <td>{tournament.prizePool}</td>
                <td>{tournament.organizer}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default UpcomingTournaments;
