import axios from 'axios';

export const fetchTournaments = async () => {
    const apiKey = '2_gNvl2m-yg3Ym-pisLvf8HJ-oRpLYLV2EL5DIT9CC88jf6Br4U'; // Replace with your actual API key
    const url = 'https://api.pandascore.co/lol/tournaments/upcoming'; // Ensure this is the correct endpoint

    try {
        const response = await axios.get(url, {
            headers: {
                'Authorization': `Bearer ${apiKey}`
            }
        });

        const tournamentsData = response.data.data || []; // Adjust according to the actual response structure

        if (tournamentsData.length === 0) {
            throw new Error('No upcoming tournaments found');
        }

        return tournamentsData;
    } catch (error) {
        console.error('Error fetching tournaments:', error); // Log the error for debugging
        if (error.response) {
            console.error('API Response:', error.response.data); // Log the API response if available
        }
        throw new Error('Error fetching tournaments: Please try again later.'); // Customize the error message
    }
};
