import React, { useEffect, useState } from 'react';
import axios from 'axios';

const LoLNews = () => {
    const [news, setNews] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchLoLNews = async () => {
            try {
                // Refined query to ensure it targets League of Legends news
                const response = await axios.get(`https://newsapi.org/v2/everything`, {
                    params: {
                        q: 'League of Legends', // Query for League of Legends
                        language: 'en',         // Language in English
                        apiKey: 'bd158c9a65504f5786aa5ca60bf0bc21' // Your API Key
                    }
                });
                // Filter articles if necessary to ensure relevance
                const filteredNews = response.data.articles.filter(article =>
                    article.title.toLowerCase().includes('league of legends') ||
                    (article.description && article.description.toLowerCase().includes('league of legends'))
                );
                setNews(filteredNews);
                setLoading(false);
            } catch (err) {
                setError('Failed to fetch League of Legends news');
                setLoading(false);
            }
        };

        fetchLoLNews();
    }, []);

    if (loading) {
        return <p>Loading League of Legends news...</p>;
    }

    if (error) {
        return <p>{error}</p>;
    }

    return (
        <div>
            <h2>League of Legends News</h2>
            {news.length > 0 ? (
                <ul>
                    {news.map((article, index) => (
                        <li key={index}>
                            <a href={article.url} target="_blank" rel="noopener noreferrer">
                                <h3>{article.title}</h3>
                            </a>
                            <p>{article.description}</p>
                            {article.urlToImage && <img src={article.urlToImage} alt={article.title} style={{ width: '100px', height: 'auto' }} />}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No League of Legends news available.</p>
            )}
        </div>
    );
};

export default LoLNews;
