import axios from 'axios';

export const fetchTournaments = async () => {
  const apiKey = '2_gNvl2m-yg3Ym-pisLvf8HJ-oRpLYLV2EL5DIT9CC88jf6Br4U'; // Replace with your actual API key
  const url = 'https://api.pandascore.io/lol/tournaments';

  try {
    const response = await axios.get(url, {
      headers: {
        'Authorization': `Bearer ${apiKey}`
      }
    });

    console.log('API Response:', response.data); // Debugging
    return response.data; // Adjust according to the actual structure
  } catch (error) {
    console.error('Error fetching tournaments:', error.message);
    throw new Error(`Error fetching tournaments: ${error.message}`);
  }
};
