import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './UpcomingMatches.css'; // Assuming you have CSS for styling

const FetchUpcomingMatches = () => {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const response = await axios.get('https://api.pandascore.co/lol/matches/upcoming', {
          headers: {
            'Authorization': 'Bearer 2_gNvl2m-yg3Ym-pisLvf8HJ-oRpLYLV2EL5DIT9CC88jf6Br4U', // Replace with your actual API key if required
          },
        });
        console.log(response.data); // Log the response data for debugging
        setMatches(response.data);
      } catch (err) {
        setError('Failed to fetch matches. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchMatches();
  }, []);

  if (loading) return <div>Loading matches...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="upcoming-matches">
      <h2>Upcoming Matches</h2>
      {(!Array.isArray(matches) || matches.length === 0) ? (
        <p>No upcoming matches available.</p>
      ) : (
        <div className="match-grid">
          {matches.map((match) => (
            <div key={match.id} className="match-item">
              <h3>Match ID: {match.id}</h3>
              <p><strong>Start Time:</strong> {new Date(match.begin_at).toLocaleString()}</p>
              <p><strong>League:</strong> {match.league?.name || 'N/A'}</p>
              <p><strong>Teams:</strong></p>
              <ul>
                {match.opponents.map((team) => (
                  <li key={team.id}>
                    <img src={team.image_url} alt={team.name} style={{ width: '50px', height: '50px' }} />
                    {team.name} (Slug: {team.slug})
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default FetchUpcomingMatches;
