import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './'

const EsportsNews = () => {
  const [news, setNews] = useState([]);

  useEffect(() => {
    const fetchEsportsNews = async () => {
      try {
        const response = await axios.get('https://esports.sportdevs.com/news', {
          headers: {
            Authorization: 'Bearer 0AAh34asVkuAYJcWp8eY4g',
          },
        });
        const filteredNews = response.data.filter((article) => article.language === 'en'); // Filter for English news
        setNews(filteredNews);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchEsportsNews();
  }, []);

  return (
    <div>
      {news.length > 0 ? (
        news.map((article) => (
          <div key={article.id}>
            <h2>{article.title}</h2>
            <p>{article.description}</p>
            {article.imageUrl && <img src={article.imageUrl} alt={article.title} width={400} />}
          </div>
        ))
      ) : (
        <p>No news available</p>
      )}
    </div>
  );
};

export default EsportsNews;
