import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import axios from 'axios';
import 'react-calendar/dist/Calendar.css'; // ensure this is imported here too

const MyCalendar = () => {
    const [date, setDate] = useState(new Date());
    const [events, setEvents] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:5000/events')
            .then(response => setEvents(response.data))
            .catch(error => console.log(error));
    }, []);

    const onDateChange = date => {
        setDate(date);
    };

    const onAddEvent = () => {
        const name = prompt('Enter event name:');
        const description = prompt('Enter event description:');

        if (name && description) {
            const newEvent = { name, date, description };

            axios.post('http://localhost:5000/events', newEvent)
                .then(response => setEvents([...events, response.data]))
                .catch(error => console.log(error));
        }
    };

    const tileContent = ({ date, view }) => {
        const currentDayEvents = events.filter(event => 
            new Date(event.date).toDateString() === date.toDateString()
        );

        return (
            currentDayEvents.length > 0 && (
                <ul>
                    {currentDayEvents.map(event => (
                        <li key={event._id}>{event.name}</li>
                    ))}
                </ul>
            )
        );
    };

    return (
        <div>
            <h2>Calendar</h2>
            <Calendar
                onChange={onDateChange}
                value={date}
                tileContent={tileContent}
            />
            <button onClick={onAddEvent}>Add Event</button>
        </div>
    );
};

export default MyCalendar;
