import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Navbar = ({ userName, setUserName }) => {
  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      await axios.post('http://localhost:3001/logout', {}, { withCredentials: true });
      console.log('Logout successful');

      setUserName(''); // Reset userName state
      navigate('/'); // Redirect to home
    } catch (error) {
      console.error("Logout failed", error);
      alert("Logout failed. Please try again."); // Display user-friendly error message
    }
  };

  return (
    <nav className='navbar'>
      <Link to='/'>
        <img src="/images/logo.png" alt="Logo" className="logo" />
      </Link>
      <div className='navbar-links'>
        <div className='nav-item dropdown'>
          <p className='dropdown-title'>World Wide Tournaments</p>
          <div className='dropdown-menu'>
            <Link to="/league-of-legends" className='dropdown-item'>League of Legends</Link>
            <Link to="/valorant" className='dropdown-item'>Valorant</Link>
            <Link to="/overwatch2" className='dropdown-item'>Overwatch 2</Link>
          </div>
        </div>
        <Link to="/guideslist" className='nav-item'>
          <p>Guides</p>
        </Link>
        <div className='nav-item'>
          <p>Forum</p>
        </div>
        <div className='nav-item'>
          <p>Gift Codes</p>
        </div>
        <Link to="/contact" className='nav-item'>
          <p>Contact</p>
        </Link>
        <button className="logout-button" onClick={onLogout}>Logout</button>
      </div>
      <div className="auth-buttons">
        {userName ? (
          <>
            <p>Welcome, {userName}!</p>
            <button className='btn logout-btn' onClick={handleLogout}>
              Logout
            </button>
          </>
        ) : (
          <>
            <Link to="/login" className='auth-button'>
              <button className='btn login-btn'>Login</button>
            </Link>
            <Link to="/signup" className='auth-button'>
              <button className='btn signup-btn'>Signup</button>
            </Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
