import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom'; // Import useHistory for navigation

const Navbar = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const history = useHistory(); // Initialize useHistory

  useEffect(() => {
    const checkLogin = async () => {
      try {
        const response = await axios.get('http://localhost:3001/profile', { withCredentials: true });
        setUsername(response.data.name);
        setIsAuthenticated(true);
      } catch (error) {
        setIsAuthenticated(false);
        setUsername('');
      }
    };
    checkLogin();
  }, []);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:3001/login', { email, password }, { withCredentials: true });
      setMessage(res.data.msg);
      setUsername(res.data.user.name);
      setIsAuthenticated(true);
      history.push('/'); // Redirect to home or desired page after login
    } catch (error) {
      setMessage('Login failed');
      console.error(error);
    }
  };

  const handleLogout = async () => {
    try {
      const res = await axios.post('http://localhost:3001/logout', {}, { withCredentials: true });
      setMessage(res.data.msg);
      setIsAuthenticated(false);
      setUsername('');
      history.push('/'); // Redirect to home or desired page after logout
    } catch (error) {
      setMessage('Logout failed');
      console.error(error);
    }
  };

  return (
    <nav style={{ padding: '10px', backgroundColor: '#f8f9fa' }}>
      <div className="logo">My App</div>
      <ul style={{ listStyleType: 'none', display: 'flex', gap: '20px' }}>
        <li><a href="/">Home</a></li>
        <li><a href="/about">About</a></li>
        <li><a href="/contact">Contact</a></li>

        {isAuthenticated ? (
          <>
            <li>Welcome, {username}!</li>
            <li>
              <button onClick={handleLogout}>Logout</button>
            </li>
          </>
        ) : (
          <>
            <li>
              <button onClick={() => history.push('/login')}>Login</button>
            </li>
            <li>
              <button onClick={() => history.push('/signup')}>Sign Up</button>
            </li>
          </>
        )}
      </ul>

      {message && <p>{message}</p>}

      {/* Login Form (Conditional Rendering) */}
      {!isAuthenticated && (
        <form onSubmit={handleLogin} style={{ marginTop: '20px' }}>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Enter email"
            required
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter password"
            required
          />
          <button type="submit">Login</button>
        </form>
      )}
    </nav>
  );
};

export default Navbar;
