import React, { useState } from 'react';
import axios from 'axios';
import Navbar from './Navbar'; // Assume Navbar is in the same directory

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/login', { email, password }, { withCredentials: true });
      // Assuming you have a context or state management to handle user data
      // e.g., setUser(response.data.user);
      window.location.reload(); // Reload the page to see the updated navbar
    } catch (err) {
      setError(err.response.data.msg);
    }
  };

  return (
    <div>
      <Navbar />
      <form onSubmit={handleLogin}>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
          required
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          required
        />
        <button type="submit">Login</button>
        {error && <p>{error}</p>}
      </form>
    </div>
  );
};

export default Login;
