// Forum.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Forum = () => {
  const [posts, setPosts] = useState([]);
  const [newPost, setNewPost] = useState({ title: '', body: '' });
  const [newComment, setNewComment] = useState({ body: '', author: '' });

  // Fetch posts when the component mounts
  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:3001/posts');
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    fetchPosts();
  }, []);

  // Handle new post submission
  const handlePostSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3001/posts', newPost);
      setPosts((prevPosts) => [response.data, ...prevPosts]);
      setNewPost({ title: '', body: '' });
    } catch (error) {
      console.error('Error submitting post:', error);
    }
  };

  // Handle new comment submission
  const handleCommentSubmit = async (postId, e) => {
    e.preventDefault();
    try {
      await axios.post(`http://localhost:3001/posts/${postId}/comments`, newComment);
      setNewComment({ body: '', author: '' });
      // Refresh posts after adding a comment
      const updatedPosts = await axios.get('http://localhost:3001/posts');
      setPosts(updatedPosts.data);
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };

  return (
    <div>
      <h1>Forum</h1>
      <form onSubmit={handlePostSubmit}>
        <input
          type="text"
          placeholder="Post Title"
          value={newPost.title}
          onChange={(e) => setNewPost({ ...newPost, title: e.target.value })}
        />
        <textarea
          placeholder="Post Body"
          value={newPost.body}
          onChange={(e) => setNewPost({ ...newPost, body: e.target.value })}
        />
        <button type="submit">Submit Post</button>
      </form>
      {posts.map(post => (
        <div key={post._id}>
          <h2>{post.title}</h2>
          <p>{post.body}</p>
          <p><strong>Comments:</strong></p>
          <ul>
            {post.comments.map(comment => (
              <li key={comment._id}>
                {comment.body} - <em>{comment.author}</em>
              </li>
            ))}
          </ul>
          <form onSubmit={(e) => handleCommentSubmit(post._id, e)}>
            <input
              type="text"
              placeholder="Your Name"
              value={newComment.author}
              onChange={(e) => setNewComment({ ...newComment, author: e.target.value })}
            />
            <textarea
              placeholder="Write a comment..."
              value={newComment.body}
              onChange={(e) => setNewComment({ ...newComment, body: e.target.value })}
            />
            <button type="submit">Submit Comment</button>
          </form>
        </div>
      ))}
    </div>
  );
};

export default Forum;
