import axios from 'axios';

export const fetchTournaments = async () => {
  const apiKey = '6PPhLJhOXgrIleMLPsAR_b_oQzTsOTC845yiOufOAyt4L-MJOwA'; // Replace with your actual API key
  const url = 'https://api.pandascore.io/lol/tournaments';

  try {
    const response = await axios.get(url, {
      headers: {
        'Authorization': `Bearer ${apiKey}`
      }
    });

    console.log('API Response:', response.data); // Debugging
    return response.data;
  } catch (error) {
    console.error('Error fetching tournaments:', error.message);
    throw new Error(`Error fettttching tournaments: ${error.message}`);
  }
};
