import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UpcomingTournaments = () => {
    const [tournaments, setTournaments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchTournaments = async () => {
            try {
                const token = process.env.REACT_APP_PANDASCORE_TOKEN; // Get the token from environment variables
                const response = await axios.get('https://api.pandascore.co/lol/tournaments/upcoming', {
                    headers: {
                        'Authorization': `Bearer ${token}` // Set the token in the Authorization header
                    }
                });
                setTournaments(response.data); // Assuming the response data is an array of tournaments
            } catch (err) {
                setError('Error fetching tournaments: ' + err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchTournaments();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <h1>Upcoming League of Legends Tournaments</h1>
            {tournaments.map((tournament) => (
                <div key={tournament.id}>
                    <h2>{tournament.league.name}</h2>
                    <p>Start: {new Date(tournament.begin_at).toLocaleString()}</p>
                    <p>End: {new Date(tournament.end_at).toLocaleString()}</p>
                    <p>
                        Matches:
                        {tournament.matches.map((match) => (
                            <div key={match.id}>
                                <h3>{match.name}</h3>
                                <p>Scheduled At: {new Date(match.scheduled_at).toLocaleString()}</p>
                                <a href={match.live.url} target="_blank" rel="noopener noreferrer">
                                    Watch Live
                                </a>
                                <div>
                                    {match.streams_list.map((stream) => (
                                        <div key={stream.raw_url}>
                                            <a href={stream.raw_url} target="_blank" rel="noopener noreferrer">
                                                {stream.language} Stream
                                            </a>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        ))}
                    </p>
                </div>
            ))}
        </div>
    );
};

export default UpcomingTournaments;
