const axios = require('axios');

const getTournaments = async () => {
  const apiKey = '6PPhLJhOXgrIleMLPsAR_b_oQzTsOTC845yiOufOAyt4L-MJOwAY'; // Replace with your actual API key
  const url = 'https://api.pandascore.io/lol/tournaments'; // Ensure this is the correct endpoint

  try {
    const response = await axios.get(url, {
      headers: {
        'Authorization': `Bearer ${apiKey}`
      }
    });

    console.log('API Response:', response.data); // Debugging
    return response.data;
  } catch (error) {
    console.error('Error fetching tournaments:', error.response ? error.response.data : error.message);
    throw new Error(`Error fetching tournaments: ${error.response ? error.response.data : error.message}`);
  }
};

module.exports = { getTournaments };
