import React, { useRef } from 'react';
import Navbar from './Navbar'
import emailjs from "@emailjs/browser";

const Contact = () => {
    const form = useRef();

    const sendEmail = (e) => {
      e.preventDefault();

      emailjs.sendForm('service_hsldulf', 'template_zd9mkgp', form.current, 'YOUR_PUBLIC_KEY')
        .then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });
    };

    return (
      <div className="contact-container">
        <Navbar />
        <div className="contact-content">
          <p className="contact-header">
            You can send us a message down below in the form and feel free to give us feedback!
          </p>       
          <form ref={form} onSubmit={sendEmail} className="contact-form">
            <label className="form-label">Name</label>
            <input type="text" name="user_name" className="form-input" required />

            <label className="form-label">Email</label>
            <input type="email" name="user_email" className="form-input" required />

            <label className="form-label">Message</label>
            <textarea name="message" className="form-textarea" required></textarea>

            <input type="submit" value="Send" className="form-submit" />        
          </form>
        </div>
      </div>
    );
  }

  export default Contact;
