import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './CreateTournamentForm.css';
import Navbar from './Navbar';
import TournamentCalendar from './TournamentCalendar'; // Import the new component

const CreateTournamentForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    game: '',
    date: '',
    time: '',
    teams: [''],
    prizePool: '',
    organizer: '',
  });

  const [successMessage, setSuccessMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const checkLoggedInUser = async () => {
      try {
        const response = await axios.get('http://localhost:3001/profile', { withCredentials: true });
        setFormData((prev) => ({ ...prev, organizer: response.data.name }));
      } catch (error) {
        navigate('/login');
      }
    };

    checkLoggedInUser();
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleTeamsChange = (index, value) => {
    const newTeams = [...formData.teams];
    newTeams[index] = value;
    setFormData({ ...formData, teams: newTeams });
  };

  const addTeam = () => {
    setFormData({ ...formData, teams: [...formData.teams, ''] });
  };

  const handleGameSelect = (game) => {
    setFormData({ ...formData, game });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3001/tournaments', formData);
      setSuccessMessage('Tournament created successfully!');
      setTimeout(() => setSuccessMessage(''), 3000);
      setFormData({ ...formData, date: '', time: '', teams: [''], prizePool: '' });
    } catch (error) {
      console.error('Error creating tournament:', error);
    }
  };

  return (
    <div>
      <Navbar />
      <div className="form-container">
        <form onSubmit={handleSubmit}>
          <h2 className='title'>Create a Tournament</h2>
          {successMessage && <div className="success-message">{successMessage}</div>}

          {/* Form fields for creating a tournament... */}

          <button type="submit">Create Tournament</button>
        </form>

        <TournamentCalendar /> {/* Include the TournamentCalendar component */}
      </div>
    </div>
  );
};

export default CreateTournamentForm;
