import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './OwUpcomingTournaments.css';

const OwUpcomingTournaments = () => {
  const [tournaments, setTournaments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTournaments = async () => {
      try {
        const response = await axios.get('https://api.pandascore.co/ow/tournaments/upcoming', {
          headers: {
            Authorization: `Bearer YOUR_API_KEY`, // Replace with your actual API key
          },
        });
        setTournaments(response.data);
      } catch (err) {
        setError('Failed to fetch tournaments. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchTournaments();
  }, []);

  if (loading) return <div>Loading tournaments...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="ow-upcoming-tournaments">
      <h2>Upcoming Overwatch Tournaments</h2>
      <div className="tournament-grid">
        {tournaments.length === 0 ? (
          <p>No upcoming tournaments available.</p>
        ) : (
          tournaments.map((tournament) => (
            <div key={tournament.id} className="tournament-item">
              <h3>{tournament.league.name}</h3>
              {tournament.league.image_url && (
                <img src={tournament.league.image_url} alt={tournament.league.name} />
              )}
              <p className="tournament-dates">
                <strong>Begin At:</strong> {new Date(tournament.begin_at).toLocaleString()}<br />
                <strong>End At:</strong> {new Date(tournament.end_at).toLocaleString()}
              </p>
              <h4>Matches:</h4>
              <div className="matches">
                {tournament.matches.map((match) => (
                  <div key={match.id} className="match-container">
                    <h5>{match.name}</h5>
                    <div className="match-details">
                      <p><strong>Type:</strong> {match.match_type} (Status: {match.status})</p>
                      <p><strong>Scheduled At:</strong> {new Date(match.scheduled_at).toLocaleString()}</p>
                      {/* Displaying team names and images */}
                      <div className="teams">
                        {match.opponents.map((team) => (
                          <div key={team.id} className="team-info">
                            {team.image_url && (
                              <img src={team.image_url} alt={team.name} style={{ width: '50px', height: '50px' }} />
                            )}
                            <span>{team.name} (Slug: {team.slug})</span>
                          </div>
                        ))}
                      </div>
                      {match.streams_list.length > 0 && match.streams_list[0].raw_url && (
                        <a className="live-link" href={match.streams_list[0].raw_url} target="_blank" rel="noopener noreferrer">
                          Watch Live
                        </a>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default OwUpcomingTournaments;
