import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Calendar from 'react-calendar'; // Import the Calendar component
import './TournamentCalendar.css'; // Create a CSS file for styling

const TournamentCalendar = () => {
  const [tournaments, setTournaments] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [selectedTournament, setSelectedTournament] = useState(null);

  useEffect(() => {
    const fetchTournaments = async () => {
      try {
        const response = await axios.get('http://localhost:3001/tournaments'); // Fetch existing tournaments
        setTournaments(response.data);
      } catch (error) {
        console.error('Error fetching tournaments:', error);
      }
    };

    fetchTournaments();
  }, []);

  const handleDateChange = (date) => {
    setSelectedDate(date);
    const formattedDate = date.toISOString().split('T')[0]; // Format date to YYYY-MM-DD
    const tournament = tournaments.find(t => t.date === formattedDate); // Find tournament by date
    setSelectedTournament(tournament);
  };

  return (
    <div className="calendar-container">
      <Calendar
        onChange={handleDateChange}
        value={selectedDate}
      />
      {selectedTournament && (
        <div className="tournament-info">
          <h3>Tournament Info:</h3>
          <p>Name: {selectedTournament.name}</p>
          <p>Game: {selectedTournament.game}</p>
          <p>Date: {selectedTournament.date}</p>
          <p>Time: {selectedTournament.time}</p>
          <p>Teams: {selectedTournament.teams.join(', ')}</p>
          <p>Prize Pool: {selectedTournament.prizePool}</p>
          <p>Organizer: {selectedTournament.organizer}</p>
        </div>
      )}
    </div>
  );
};

export default TournamentCalendar;
