import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EsportsNews = () => {
  const [news, setNews] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchEsportsNews = async () => {
      try {
        const response = await axios.get('https://esports.sportdevs.com/news', {
          headers: {
            'Authorization': 'Bearer 0AAh34asVkuAYJcWp8eY4g'
          }
        });
        console.log('API Response:', response.data); // Log the entire response to inspect its structure

        // Adjust this based on the actual structure of the API response
        setNews(response.data.articles || response.data.news || []); // Update based on actual data structure
      } catch (err) {
        setError('Error fetching data');
        console.error('Error fetching data:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchEsportsNews();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      {news.length === 0 ? (
        <div>No news available</div>
      ) : (
        news.map((item, index) => (
          <div key={index}>
            <h2>{item.title}</h2>
            <p>{item.description}</p>
            <a href={item.url} target="_blank" rel="noopener noreferrer">Read more</a>
          </div>
        ))
      )}
    </div>
  );
};

export default EsportsNews;
