import axios from 'axios';


export const fetchTournaments = async () => {
    const apiKey = '2_gNvl2m-yg3Ym-pisLvf8HJ-oRpLYLV2EL5DIT9CC88jf6Br4U';
    const url = 'https://api.pandascore.co/lol/tournaments/upcoming';

    try {
      const response = await axios.get(url, {
        headers: {
          'Authorization': `Bearer ${apiKey}`
        }
      });

      const tournamentsData = response.data.data || [];

      if (tournamentsData.length === 0) {
        throw new Error('No upcoming tournaments found');
      }

      return tournamentsData;
    } catch (error) {
      console.error('Error fetching tournaments:', error);
      if (error.response) {
        console.error('API Response:', error.response.data);
      }
      throw new Error('Error fetching tournaments: Please try again later.');
    }
  };
