import React, { useState, useEffect } from 'react';
import { fetchTournaments } from './tournamentService'; // Ensure this path is correct
import './TournamentList.css'; // Import the CSS file

const TournamentList = () => {
    const [tournaments, setTournaments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const getTournaments = async () => {
            try {
                const response = await fetchTournaments();
                console.log(response); // Inspect the structure of the response
                setTournaments(response || []); // Adjust according to actual data structure
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        getTournaments();
    }, []);

    if (loading) return <p className="loading-message">Loading...</p>;
    if (error) return <p className="error-message">Error: {error}</p>;

    return (
        <div className="container">
            <h1 className="title">Upcoming Tournaments</h1>
            <div className="tournament-grid">
                {tournaments.length > 0 ? (
                    tournaments.map(tournament => (
                        <div className="tournament-card" key={tournament.id}>
                            <h2 className="tournament-name">{tournament.name}</h2>
                            {/* Ensure tournament.image_url is a valid URL */}
                            {tournament.image_url ? (
                                <img className="tournament-image" src={tournament.image_url} alt={tournament.name} />
                            ) : (
                                <p>No image available</p>
                            )}
                            <p>URL: <a className="tournament-link" href={tournament.url} target="_blank" rel="noopener noreferrer">{tournament.url}</a></p>
                            <p className="tournament-slug">Slug: {tournament.slug}</p>
                            {/* Add more properties as needed */}
                        </div>
                    ))
                ) : (
                    <p className="no-tournaments-message">No upcoming tournaments found.</p>
                )}
            </div>
        </div>
    );
};

export default TournamentList;
