import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Forum.css';
import Navbar from './Navbar';

const Forum = () => {
  const [posts, setPosts] = useState([]);
  const [newPost, setNewPost] = useState({ title: '', body: '' });
  const [newComment, setNewComment] = useState({ body: '', author: '' });

  // Fetch posts when the component mounts
  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      const response = await axios.get('http://localhost:3001/posts');
      setPosts(response.data);
    } catch (error) {
      console.error('Error fetching posts:', error);
    }
  };

  // Handle new post submission
  const [loading, setLoading] = useState(false);
const [error, setError] = useState(null);

const handlePostSubmit = async (e) => {
  e.preventDefault();
  setLoading(true);
  setError(null);

  try {
    const response = await fetch('/posts', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newPost),
    });

    if (!response.ok) {
      throw new Error('Failed to submit post');
    }

    const savedPost = await response.json();
    setPosts((prevPosts) => [savedPost, ...prevPosts]);
    setNewPost({ title: '', body: '' });
  } catch (error) {
    setError(error.message);
  } finally {
    setLoading(false);
  }
};

// Display loading and error states
return (
  <div>
    {loading && <p>Loading...</p>}
    {error && <p>Error: {error}</p>}
    {/* Your existing form and post display code */}
  </div>
);

  // Handle new comment submission
  const handleCommentSubmit = async (postId, e) => {
    e.preventDefault();
    try {
      await axios.post(`http://localhost:3001/posts/${postId}/comments`, newComment);
      setNewComment({ body: '', author: '' });
      fetchPosts();  // Refresh the posts after adding a comment
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };

  return (
    <div className='forum-container'>
        <Navbar />
    <h1>Forum</h1>

    {/* Post submission form */}
    <form onSubmit={handlePostSubmit}>
      <input
        type="text"
        placeholder="Post Title"
        value={newPost.title}
        onChange={(e) => setNewPost({ ...newPost, title: e.target.value })}
      />
      <textarea
        placeholder="Post Body"
        value={newPost.body}
        onChange={(e) => setNewPost({ ...newPost, body: e.target.value })}
      />
      <button type="submit">Submit Post</button>
    </form>

    {/* Displaying posts */}
    {posts.map(post => (
      <div key={post._id} className="post">
        <h2>{post.title}</h2>
        <p>{post.body}</p>

        <p><strong>Comments:</strong></p>
        <ul>
          {post.comments.map(comment => (
            <li key={comment._id}>
              {comment.body} - <em>{comment.author}</em>
            </li>
          ))}
        </ul>

        {/* Comment submission form */}
        <form className="comment-form" onSubmit={(e) => handleCommentSubmit(post._id, e)}>
          <input
            type="text"
            placeholder="Your Name"
            value={newComment.author}
            onChange={(e) => setNewComment({ ...newComment, author: e.target.value })}
          />
          <textarea
            placeholder="Write a comment..."
            value={newComment.body}
            onChange={(e) => setNewComment({ ...newComment, body: e.target.value })}
          />
          <button type="submit">Submit Comment</button>
        </form>
      </div>
    ))}
  </div>
  );
};

export default Forum;
