import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Navbar = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [message, setMessage] = useState('');
  const [username, setUsername] = useState('');

  useEffect(() => {
    // Check if user is logged in when the component mounts
    axios.get('http://localhost:3001/profile', { withCredentials: true })
      .then((response) => {
        setIsAuthenticated(true);
        setUsername(response.data.name);
      })
      .catch(() => {
        setIsAuthenticated(false);
      });
  }, []);

  const handleLogout = async () => {
    try {
      await axios.post('http://localhost:3001/logout', {}, { withCredentials: true });
      setIsAuthenticated(false);
      setUsername('');
      navigate('/'); // Redirect to home or another route after logout
    } catch (error) {
      console.error("Logout failed", error);
      alert("Logout failed. Please try again.");
    }
  };


  return (
    <nav className='navbar'>
      <Link to='/'>
        <img src="/images/logo.png" alt="Logo" className="logo" />
      </Link>

      <div className='navbar-links'>
        <div className='nav-item dropdown'>
          <p className='dropdown-title'>World Wide Tournaments</p>
          <div className='dropdown-menu'>
            <Link to="/league-of-legends" className='dropdown-item'>
              League of Legends
            </Link>
            <Link to="/valorant" className='dropdown-item'>
              Valorant
            </Link>
            <Link to="/overwatch2" className='dropdown-item'>
              Overwatch 2
            </Link>
          </div>
        </div>

        <Link to="/guideslist" className='nav-item'>
          <p>Guides</p>
        </Link>
        <div className='nav-item'>
          <p>Forum</p>
        </div>
        <div className='nav-item'>
          <p>Gift Codes</p>
        </div>
        <Link to="/contact" className='nav-item'>
          <p>Contact</p>
        </Link>
      </div>

      <div className="auth-buttons">
        {isAuthenticated ? (
          <div className='welcome-user'>
            <p>{username}!</p>
            <button className='btn logout-btn' onClick={handleLogout}>Logout</button>
          </div>
        ) : (
          <>
            <Link to="/login" className='auth-button'>
              <button className='btn login-btn'>Login</button>
            </Link>
            <Link to="/signup" className='auth-button'>
              <button className='btn signup-btn'>Signup</button>
            </Link>
          </>
        )}
      </div>

      {message && <p style={{ color: 'yellow', marginTop: '10px' }}>{message}</p>}
    </nav>
  );
};

export default Navbar;
