const TournamentList = () => {
    const [tournaments, setTournaments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
      const getTournaments = async () => {
        try {
          const data = await fetchTournaments();
          setTournaments(data);
        } catch (err) {
          setError(err.message);
        } finally {
          setLoading(false);
        }
      };

      getTournaments();
    }, []);

    const groupStages = (stages) => {
      // Group similar stages together for better presentation
      const grouped = stages.reduce((acc, stage) => {
        const category = stage.includes('Group') ? 'Groups' : stage.includes('Playoffs') ? 'Playoffs' : 'Others';
        if (!acc[category]) acc[category] = [];
        acc[category].push(stage);
        return acc;
      }, {});

      return grouped;
    };

    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error: {error}</p>;

    const groupedTournaments = groupStages(tournaments);

    return (
      <div>
        <h1>Tournaments</h1>
        {Object.keys(groupedTournaments).map((category) => (
          <div key={category}>
            <h2>{category}</h2>
            <ul>
              {groupedTournaments[category].map((stage, index) => (
                <li key={index}>{stage}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    );
  };
