import React, { useState, useEffect } from 'react';
import { fetchTournaments } from './tournamentService'; // Ensure this path is correct
import './TournamentList.css'; // Import your CSS file

const TournamentList = () => {
    const [tournaments, setTournaments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const getTournaments = async () => {
            try {
                const response = await fetchTournaments();
                console.log(response); // Inspect the structure of the response
                setTournaments(response || []); // Adjust according to actual data structure
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        getTournaments();
    }, []);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error: {error}</p>;

    return (
        <div>
            <h1>Upcoming Tournaments</h1>
            <ul>
                {tournaments.length > 0 ? (
                    tournaments.map(tournament => (
                        <li key={tournament.id}>
                            <h2>{tournament.name}</h2>
                            {tournament.image_url && <img src={tournament.image_url} alt={tournament.name} />}
                            <p>URL: <a href={tournament.url} target="_blank" rel="noopener noreferrer">{tournament.url}</a></p>
                            <p>Slug: {tournament.slug}</p>
                        </li>
                    ))
                ) : (
                    <p>No upcoming tournaments found.</p>
                )}
            </ul>
        </div>
    );
};

export default TournamentList; // Correct placement of export default
