import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './GamesNews.css';

const GamesNews = () => {
    const [news, setNews] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const apiKey = 'bd158c9a65504f5786aa5ca60bf0bc21'; // Your NewsAPI Key
    const games = [
        'League of Legends',
        'Warzone',
        'Valorant',
        'Fortnite'
    ];

    const trustedSources = ['IGN', 'GameSpot', 'Kotaku', 'Polygon', 'PC Gamer']; // Trusted sources

    useEffect(() => {
        const fetchNewsForGames = async () => {
            try {
                const newsPromises = games.map(game =>
                    axios.get('https://newsapi.org/v2/everything', {
                        params: {
                            q: game,
                            language: 'en',
                            apiKey: apiKey
                        }
                    })
                );

                const responses = await Promise.all(newsPromises);

                const newsObject = responses.reduce((acc, response, index) => {
                    const game = games[index];
                    let articles = response.data.articles;

                    // Apply filters
                    articles = filterByDate(articles);
                    articles = filterBySource(articles);

                    acc[game] = articles.slice(0, 5); // Show only top 5 articles
                    return acc;
                }, {});

                setNews(newsObject);
                setLoading(false);
            } catch (err) {
                setError('Failed to fetch news');
                setLoading(false);
            }
        };

        fetchNewsForGames();
    }, []);

    const filterByDate = (articles, daysAgo = 7) => {
        const cutoffDate = new Date();
        cutoffDate.setDate(cutoffDate.getDate() - daysAgo);

        return articles.filter(article => {
            const articleDate = new Date(article.publishedAt);
            return articleDate >= cutoffDate;
        });
    };

    const filterBySource = (articles) => {
        return articles.filter(article => trustedSources.includes(article.source.name));
    };

    if (loading) {
        return <p>Loading news...</p>;
    }

    if (error) {
        return <p>{error}</p>;
    }

    return (
        <div>
            {games.map(game => (
                <div key={game}>
                    <h2>{game} News</h2>
                    {news[game] && news[game].length > 0 ? (
                        <ul>
                            {news[game].map((article, index) => (
                                <li key={index}>
                                    <a href={article.url} target="_blank" rel="noopener noreferrer">
                                        <h3>{article.title}</h3>
                                    </a>
                                    <p>{article.description}</p>
                                    {article.urlToImage && <img src={article.urlToImage} alt={article.title} style={{ width: '100px', height: 'auto' }} />}
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <p>No news available for {game}.</p>
                    )}
                </div>
            ))}
        </div>
    );
};

export default GamesNews;
