import React, { useState, useEffect } from 'react';

const TournamentList = () => {
  const [tournaments, setTournaments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getTournaments = async () => {
        const apiKey = 'x2KLlXyc2necjP5qsqMyJdKqWEeCTmsXa-L0lsLakgbiLS7wr_o'; // Replace with your Pandascore API key
        const url = 'https://api.pandascore.co/lol/tournaments';

        try {
          const response = await axios.get(url, {
            headers: {
              'Authorization': `Bearer ${apiKey}`
            }
          });

          return response.data;
        } catch (error) {
          console.error('Error fetching tournaments:', error.response ? error.response.data : error.message);
          throw error; // Ensure that the error is thrown to be handled by the route
        }
      };


    getTournaments();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <h1>Tournaments</h1>
      <ul>
        {tournaments.length > 0 ? (
          tournaments.map(tournament => (
            <li key={tournament.id}>
              <h2>{tournament.name}</h2>
              <p>League: {tournament.league}</p>
              <p>Prizepool: ${tournament.prizepool}</p>
              <p>Start Date: {new Date(tournament.begin_at).toLocaleDateString('en-US', {
                  year: 'numeric',
                  month: 'long',
                  day: 'numeric',
              })}</p>
              <p>End Date: {new Date(tournament.end_at).toLocaleDateString('en-US', {
                  year: 'numeric',
                  month: 'long',
                  day: 'numeric',
              })}</p>
              {/* Add more properties as needed */}
            </li>
          ))
        ) : (
          <p>No tournaments found.</p>
        )}
      </ul>
    </div>
  );
};

export default TournamentList; // Correct placement of export default
