import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Navbar from './Navbar';
import Banner from './Banners';
import GamesNews from './GamesNews';
import Homevideo from './Homevideo';
import UpcomingTournaments from './UpcomingTournaments';
import ThreadsList from './ThreadsList'; // Import your forum components
import ThreadDetail from './ThreadDetail';

const Home = () => {
  return (
    <Router>
      <div>
        <Navbar />
        <Switch>
          {/* Route for the home page */}
          <Route exact path="/">
            <Homevideo />
            <Banner />
            <UpcomingTournaments />
            <GamesNews />
          </Route>

          {/* Route for the forum list */}
          <Route exact path="/forum">
            <ThreadsList />
          </Route>

          {/* Route for individual thread details */}
          <Route path="/forum/:id">
            <ThreadDetail />
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default Home;
