import axios from 'axios';

export const fetchTournaments = async () => {
  try {
    const response = await axios.get('https://api.pandascore.io/lol/tournaments', {
      headers: {
        'Authorization': 'Bearer x2KLlXyc2necjP5qsqMyJdKqWEeCTmsXa-L0lsLakgbiLS7wr_o' // Replace with your actual API key
      }
    });
    return response.data; // Adjust according to the actual response
  } catch (error) {
    console.error('Error fetching tournaments:', error); // Log the full error
    throw new Error(error.message);
}
};
