import React, { useState, useEffect } from 'react';
import { fetchTournaments } from './tournamentService'; // Ensure this path is correct

const TournamentList = () => {
  const [tournaments, setTournaments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getTournaments = async () => {
      try {
        const data = await fetchTournaments();
        setTournaments(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    getTournaments();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <h1>Tournaments</h1>
      <ul>
        {tournaments.map(tournament => (
          <li key={tournament.id}>
            <h2>{tournament.name}</h2>
            <p>League: {tournament.league}</p>
            <p>Prizepool: ${tournament.prizepool}</p>
            <p>Start Date: {new Date(tournament.begin_at).toLocaleDateString()}</p>
            <p>End Date: {new Date(tournament.end_at).toLocaleDateString()}</p>
            {/* Add more properties as needed */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TournamentList;
