import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Profile.css';

const Profile = () => {
  const [userData, setUserData] = useState({
    name: '',
    email: '',
    bio: '',
    profilePicture: ''
  });

  const [formData, setFormData] = useState({
    bio: '',
    profilePicture: null
  });

  useEffect(() => {
    axios.get('http://localhost:3001/profile', { withCredentials: true })
      .then((response) => {
        setUserData(response.data);
        setFormData({ bio: response.data.bio, profilePicture: null });
      })
      .catch((error) => {
        console.error('Error fetching user data:', error);
      });
  }, []);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData({
      ...formData,
      [name]: files ? files[0] : value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formDataToSend = new FormData();
    formDataToSend.append('bio', formData.bio);
    if (formData.profilePicture) {
      formDataToSend.append('profilePicture', formData.profilePicture);
    }

    try {
      const response = await axios.put('http://localhost:3001/profile', formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
        withCredentials: true
      });
      setUserData(response.data);
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  return (
    <div className='profile-container'>
      <h2>Profile</h2>
      <div className='profile-info'>
        <img src={`http://localhost:3001/${userData.profilePicture}`} alt="Profile" className='profile-picture' />
        <p>Name: {userData.name}</p>
        <p>Email: {userData.email}</p>
        <p>Bio: {userData.bio}</p>
      </div>
      <form onSubmit={handleSubmit} encType='multipart/form-data'>
        <div className='form-group'>
          <label>Update Bio:</label>
          <textarea
            name='bio'
            value={formData.bio}
            onChange={handleChange}
            rows='4'
          />
        </div>
        <div className='form-group'>
          <label>Update Profile Picture:</label>
          <input
            type='file'
            name='profilePicture'
            onChange={handleChange}
          />
        </div>
        <button type='submit' className='btn save-btn'>Save Changes</button>
      </form>
    </div>
  );
};

export default Profile;
