import axios from "axios";

const TournamentList = () => {
  const [tournaments, setTournaments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getTournaments = async () => {
      try {
        const response = await fetchTournaments();
        console.log('Fetched tournaments:', response); // Debugging
        // Adjust this based on the actual structure of the response data
        setTournaments(response.tournaments || []);
      } catch (err) {
        console.error('Error:', err.message); // Debugging
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    getTournaments();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <h1>Tournaments</h1>
      <ul>
        {tournaments.length > 0 ? (
          tournaments.map(tournament => (
            <li key={tournament.id}>
              <h2>{tournament.name}</h2>
              <p>League: {tournament.league}</p>
              <p>Prizepool: ${tournament.prizepool}</p>
              <p>Start Date: {new Date(tournament.begin_at).toLocaleDateString()}</p>
              <p>End Date: {new Date(tournament.end_at).toLocaleDateString()}</p>
            </li>
          ))
        ) : (
          <p>No tournaments found.</p>
        )}
      </ul>
    </div>
  );
};

export default TournamentList;
