import axios from 'axios';

export const fetchTournaments = async () => {
    const url = 'https://api.pandascore.co/lol/tournaments/past'; // Ensure this points to your backend

    try {
        const response = await axios.get(url);
        return response.data; // Ensure this matches the expected structure
    } catch (error) {
        console.error('Error fetching tournaments:', error.message); // Log the error for debugging
        throw new Error('Error fetching tournaments: Please try again later.'); // Customize the error message
    }
};
