// tournamentService.js
import axios from 'axios';

export const fetchTournaments = async () => {
  const apiKey = 'YOUR_PANDASCORE_API_KEY'; // Replace with your actual API key
  const url = 'https://api.pandascore.co/lol/tournaments';

  try {
    const response = await axios.get(url, {
      headers: {
        'Authorization': `Bearer ${apiKey}`
      }
    });

    return response.data;
  } catch (error) {
    console.error('Error fetching tournaments:', error.response ? error.response.data : error.message);
    throw error;
  }
};
