import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Import useNavigate
import axios from 'axios';

const Navbar = ({ userName, onLogout }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState('');
  const navigate = useNavigate(); // Initialize navigate

  useEffect(() => {
    axios.get('http://localhost:3001/profile', { withCredentials: true })
      .then((response) => {
        setIsAuthenticated(true);
        setUsername(response.data.name);
      })
      .catch(() => {
        setIsAuthenticated(false);
      });
  }, []);

  const handleLogout = async () => {
    try {
      await onLogout(); // Trigger the logout passed from parent
      setIsAuthenticated(false);
      setUsername('');
      navigate('/'); // Redirect after logout
    } catch (error) {
      console.error("Logout failed", error);
    }
  };

  return (
    <nav className='navbar'>
      <Link to='/'>
        <img src="/images/logo.png" alt="Logo" className="logo" />
      </Link>

      <div className='navbar-links'>
        <div className='nav-item dropdown'>
          <p className='dropdown-title'>World Wide Tournaments</p>
          <div className='dropdown-menu'>
            <Link to="/league-of-legends" className='dropdown-item'>League of Legends</Link>
            <Link to="/valorant" className='dropdown-item'>Valorant</Link>
            <Link to="/overwatch2" className='dropdown-item'>Overwatch 2</Link>
          </div>
        </div>

        <Link to="/guideslist" className='nav-item'>
          <p>Guides</p>
        </Link>
        <div className='nav-item'>
          <p>Forum</p>
        </div>
        <div className='nav-item'>
          <p>Gift Codes</p>
        </div>
        <Link to="/contact" className='nav-item'>
          <p>Contact</p>
        </Link>
      </div>

      <div className="auth-buttons">
        {isAuthenticated || userName ? ( // Show logout if authenticated
          <div className='welcome-user'>
            <p>{userName || username}</p> {/* Only the name, no "Welcome" */}
            <button className='btn logout-btn' onClick={handleLogout}>Logout</button>
          </div>
        ) : (
          <>
            <Link to="/login" className='auth-button'>
              <button className='btn login-btn'>Login</button>
            </Link>
            <Link to="/signup" className='auth-button'>
              <button className='btn signup-btn'>Signup</button>
            </Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
