import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './UpcomingTournaments.css'; // Assuming you have CSS for styling

const FetchUpcomingTournaments = () => {
  const [tournaments, setTournaments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTournaments = async () => {
      try {
        const response = await axios.get('https://api.pandascore.co/lol/tournaments/upcoming', {
          headers: {
            'Authorization': 'Bearer YOUR_API_KEY', // Replace with your actual API key if required
          },
        });
        console.log(response.data); // Log the response data
        setTournaments(response.data);
      } catch (err) {
        setError('Failed to fetch tournaments. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchTournaments();
  }, []);

  if (loading) return <div>Loading tournaments...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="upcoming-tournaments">
      <h2>Upcoming Tournaments</h2>
      {(!Array.isArray(tournaments) || tournaments.length === 0) ? (
        <p>No upcoming tournaments available.</p>
      ) : (
        <div className="tournament-grid">
          {tournaments.map((tournament) => (
            <div key={tournament.id} className="tournament-item">
              <img src={tournament.image_url} alt={tournament.name} />
              <h3>{tournament.name}</h3>
              <p>Game: {tournament.game?.name || 'N/A'}</p>
              <p>Date: {new Date(tournament.begin_at).toLocaleDateString()}</p>
              <p>Time: {new Date(tournament.begin_at).toLocaleTimeString()}</p>
              <p>Organizer: {tournament.organizer?.name || 'N/A'}</p>
              <p>Prize Pool: ${tournament.prize_pool || 'N/A'}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default FetchUpcomingTournaments;
