import React, { useState } from 'react'; // Import useState

const Navbar = () => {
  const [isFixed, setIsFixed] = useState(false); // Initialize state

  // ... rest of your Navbar code

  useEffect(() => {
    // Add event listener on component mount
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll); // Cleanup
  }, []); // Run only on component mount

  const handleScroll = () => {
    const scrollY = window.scrollY; // Get current scroll position
    setIsFixed(scrollY > 0); // Update state based on scroll position
  };

  return (
    <div>
      <div className={`navbar ${isFixed ? 'fixed' : ''}`}>
        <img src="/images/logowhite.png" alt="Logo" className="logo" />
        <div className='navbar-links'>
          <div className='guides'>
            <Link to ="/guideslist">
            <p>Guides</p>
            </Link>
          </div>
          <div className='walkthrough'>
            <p>Walkthrough</p>
          </div>
          <div className='forum'>
            <p>Forum</p>
          </div>
          <div className='giftcodes'>
            <p>Gift Codes</p>
          </div>
        </div>
        <div className="profile-section">
          <img src="/images/profilelogo.png" alt='profilelogo' className='profilelogo' />
          <p>Sign In</p>
        </div>
      </div>
    </div>
  );
}
export default Navbar;
