const Homevideo = ({ src, controls = false, autoplay = true, loop = true, muted = true }) => {
    // Create the YouTube URL with parameters
    const videoSrc = `${src}?autoplay=${autoplay ? 1 : 0}&loop=${loop ? 1 : 0}&controls=${controls ? 1 : 0}&mute=${muted ? 1 : 0}&playlist=${src.split('/').pop()}`;

    return (
        <div style={{ margin: 0, padding: 0, maxWidth: '100%', overflow: 'hidden' }}>
            <iframe
                width="100%"
                height="800px"
                src={videoSrc}
                frameBorder="0"
                allow="autoplay; loop; muted"
                allowFullScreen
                style={{ display: 'block', width: '100vw', height: '800px', margin: 0, padding: 0, objectFit: 'cover' }}
            ></iframe>
        </div>
    );
};

export default Homevideo;
