// Footer.js
import React, { useRef } from "react";
import emailjs from "@emailjs/browser";
import Navbar from "./Navbar";

const Footer = ({ cart }) => {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "service_hsldulf",
        "template_zd9mkgp",
        form.current,
        "MzycEju2X270zHi3Z"
      )
      .then(
        (result) => {
          console.log(result.text);
          console.log("message sent");
        },
        (error) => {
          console.log(error.text);
        }
      );
  };

  return (
    <footer className="footer-container">
    <div className="footer-content">
      <div className="footer-logos">
        <a href="https://facebook.com" target="_blank" rel="noopener noreferrer">
          <img src="facebook-logo.png" alt="Facebook" className="footer-logo" />
        </a>
        <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">
          <img src="instagram-logo.png" alt="Instagram" className="footer-logo" />
        </a>
        <a href="https://youtube.com" target="_blank" rel="noopener noreferrer">
          <img src="youtube-logo.png" alt="YouTube" className="footer-logo" />
        </a>
        <a href="mailto:your-email@example.com">
          <img src="email-logo.png" alt="Email" className="footer-logo" />
        </a>
      </div>
      <div className="footer-form">
        <p style={{ fontFamily: 'fantasy', fontSize: '30px', fontWeight: 'bold', color: 'white' }}>
          You can send us a message down below in the form and feel free to give us feedback!
        </p>
        <form ref={form} onSubmit={sendEmail} style={{ marginTop: '20px' }}>
          <label htmlFor="user_name" className="form-label">Name</label>
          <input type="text" id="user_name" name="user_name" className="form-input" />

          <label htmlFor="user_email" className="form-label">Email</label>
          <input type="email" id="user_email" name="user_email" className="form-input" />

          <label htmlFor="message" className="form-label">Message</label>
          <textarea id="message" name="message" className="form-textarea"></textarea>

          <input type="submit" value="Send" className="form-submit" />
        </form>
      </div>
    </div>
  </footer>

  );
};

export default Footer;
