import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './UpcomingTournaments.css';

const UpcomingTournaments = () => {
  const [lolTournaments, setLolTournaments] = useState([]);
  const [owTournaments, setOwTournaments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const API_KEY = '2_gNvl2m-yg3Ym-pisLvf8HJ-oRpLYLV2EL5DIT9CC88jf6Br4U'; // Replace with your actual API key

  useEffect(() => {
    const fetchTournaments = async () => {
      try {
        const [lolResponse, owResponse] = await Promise.all([
          axios.get('https://api.pandascore.co/lol/tournaments/upcoming', {
            headers: { Authorization: `Bearer ${API_KEY}` },
          }),
          axios.get('https://api.pandascore.co/ow/tournaments/upcoming', {
            headers: { Authorization: `Bearer ${API_KEY}` },
          }),
        ]);
        setLolTournaments(lolResponse.data);
        setOwTournaments(owResponse.data);
      } catch (err) {
        setError('Failed to fetch tournaments. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchTournaments();
  }, [API_KEY]);

  if (loading) return <div>Loading tournaments...</div>;
  if (error) return <div>{error}</div>;

  // Combined matches for display
  const combinedTournaments = [...lolTournaments, ...owTournaments];

  return (
    <div className="upcoming-tournaments">
      <h2>Upcoming Tournaments</h2>
      {combinedTournaments.map((tournament) => (
        <div key={tournament.id} className="tournament-container">
          {tournament.league && tournament.league.image_url && (
            <img
              src={tournament.league.image_url}
              alt={tournament.league.name}
              className="league-image"
            />
          )}
          <h3>{tournament.league ? tournament.league.name : 'League Not Available'}</h3>
          {tournament.matches.map((match) => (
            <div key={match.id} className="match-container">
              <h4>{match.name}</h4>
              <p>Scheduled at: {new Date(match.scheduled_at).toLocaleString()}</p>
              <p>Match Type: {match.match_type}</p>
              <p>Teams: {match.winner_id ? match.winner_id : 'Not yet determined'}</p>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default UpcomingTournaments;
