import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Navbar = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [username, setUsername] = useState('');

  useEffect(() => {
    const checkLogin = async () => {
      try {
        const response = await axios.get('http://localhost:3001/profile', { withCredentials: true });
        setUsername(response.data.name);
        setIsAuthenticated(true);
      } catch (error) {
        setIsAuthenticated(false);
        setUsername('');
      }
    };
    checkLogin();
  }, []);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:3001/login', { email, password }, { withCredentials: true });
      setMessage(res.data.msg);
      setIsAuthenticated(true);
      setUsername(res.data.user.name);
    } catch (error) {
      setMessage('Login failed');
      console.error(error);
    }
  };

  const handleLogout = async () => {
    try {
      const res = await axios.post('http://localhost:3001/logout', {}, { withCredentials: true });
      setMessage(res.data.msg);
      setIsAuthenticated(false);
      setUsername('');
    } catch (error) {
      setMessage('Logout failed');
      console.error(error);
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto', textAlign: 'center' }}>
      <h1>My App</h1>
      {message && <p>{message}</p>}

      {!isAuthenticated ? (
        <form onSubmit={handleLogin}>
          <div>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter email"
              required
            />
          </div>
          <div>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter password"
              required
            />
          </div>
          <button type="submit">Login</button>
        </form>
      ) : (
        <div>
          <h2>Welcome, {username}!</h2>
          <button onClick={handleLogout}>Logout</button>
        </div>
      )}
    </div>
  );
};

export default Navbar;
