import React, { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom'; // Ensure useNavigate is imported
import './Login.css';
import Navbar from './Navbar';


const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [userName, setUserName] = useState('');
  const navigate = useNavigate(); // Initialize navigate function

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleLogin = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.post('http://localhost:3001/login', {
        email: formData.email, // Use formData to get the email
        password: formData.password // Use formData to get the password
      }, { withCredentials: true });

      if (response.status === 200) {
        // Set username if login is successful
        setUserName(response.data.username); // Adjust based on your API response
        setSuccess('Login successful');
        navigate('/'); // Redirect to the home page after successful login
      }
    } catch (error) {
      console.error('Login failed:', error);
      setError('Login failed. Please check your credentials.');
    }
  };

const handleLogout = async () => {
  try {
    await axios.post('http://localhost:3001/logout', {}, { withCredentials: true });
    navigate('/'); // Redirect to home after logout
  } catch (error) {
    console.error("Logout failed", error);
    alert("Logout failed. Please try again.");
  }
};



  return (
    <div>
      <Navbar userName={userName} onLogout={handleLogout} />
      <div className='background'>
        <div className="login-container">
          <h2>Login</h2>
          {error && <p className="error">{error}</p>}
          {success && <p className="success">{success}</p>}

          <form onSubmit={handleLogin}> {/* Updated to handleLogin */}
            <div className="form-group">
              <label>Email:</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Password:</label>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                required
              />
            </div>
            <button type="submit" className="login-button">Login</button>
          </form>

          <p className="signup-redirect">
            Don't have an account? <Link to="/signup">Sign up</Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
