import React, { useState, useEffect } from 'react';
import { fetchTournamentsByURL } from './tournamentService';
import './TournamentList.css';

const TournamentList = ({ game }) => {
    const [tournaments, setTournaments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const getTournaments = async () => {
            try {
                let url = '';
                if (game === 'lol') {
                    url = 'https://api.pandascore.co/lol/tournaments/upcoming';
                } else if (game === 'overwatch2') {
                    url = 'https://api.pandascore.co/ow/tournaments';
                } else if (game === 'valorant') {
                    url = 'https://api.pandascore.co/valorant/tournaments/upcoming';
                }
                const response = await fetchTournamentsByURL(url);
                setTournaments(response || []);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        getTournaments();
    }, [game]);

    if (loading) return <p className="loading-message">Loading...</p>;
    if (error) return <p className="error-message">Error: {error}</p>;

    return (
        <div className="container">
            <h1 className="title">Upcoming Tournaments for {game.toUpperCase()}</h1>
            <div className="tournament-grid">
                {tournaments.length > 0 ? (
                    tournaments.map(tournament => (
                        <div className="tournament-card" key={tournament.id}>
                            <h2 className="tournament-name">{tournament.name}</h2>
                            <img className="tournament-image" src={tournament.image_url} alt={tournament.name} />
                            <p className="tournament-date">Date: {new Date(tournament.begin_at).toLocaleDateString()}</p>
                        </div>
                    ))
                ) : (
                    <p className="no-tournaments-message">No upcoming tournaments found.</p>
                )}
            </div>
        </div>
    );
};

export default TournamentList;
