import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Profile.css';

const allGames = [
  'League of Legends',
  'Valorant',
  'Apex Legends',
  'Rocket League',
  'Rainbow Six Siege',
  'Counter Strike 2',
  'Warzone',
  'Fortnite',
  'Overwatch 2'
];

const Profile = () => {
  const [userData, setUserData] = useState({
    name: '',
    email: '',
    bio: '',
    profilePicture: '',
    favoriteGames: [] // Initialize as an empty array
  });

  const [formData, setFormData] = useState({
    bio: '',
    profilePicture: null,
    favoriteGames: [] // Initialize as an empty array
  });

  useEffect(() => {
    axios.get('http://localhost:3001/profile', { withCredentials: true })
      .then((response) => {
        setUserData(response.data);
        setFormData({ bio: response.data.bio, profilePicture: null, favoriteGames: response.data.favoriteGames || [] });
      })
      .catch((error) => {
        console.error('Error fetching user data:', error);
      });
  }, []);

  const handleGameToggle = (game) => {
    setFormData(prevFormData => {
      const newFavoriteGames = prevFormData.favoriteGames.includes(game)
        ? prevFormData.favoriteGames.filter(favGame => favGame !== game)
        : [...prevFormData.favoriteGames, game];

      return {
        ...prevFormData,
        favoriteGames: newFavoriteGames
      };
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const dataToSend = new FormData();
    dataToSend.append('bio', formData.bio);
    if (formData.profilePicture) {
      dataToSend.append('profilePicture', formData.profilePicture);
    }
    dataToSend.append('favoriteGames', JSON.stringify(formData.favoriteGames));

    try {
      const response = await axios.put('http://localhost:3001/profile', dataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
        withCredentials: true
      });
      setUserData(response.data);
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  return (
    <div className='profile-container'>
      <h2>Profile</h2>
      <div className='profile-info'>
      <img
  src={`http://localhost:3001/uploads/${userData.profilePicture}`} alt="Profile" className='profile-picture' />
        <p>{userData.name}</p>
        <p>{userData.email}</p>
        <p>{userData.bio}</p>
        <div className='favorite-games'>
          <h3>Favorite Games</h3>
          <div className='games-container'>
            {allGames.map((game) => (
              <div
                key={game}
                className={`game-item ${formData.favoriteGames.includes(game) ? 'selected' : ''}`}
                onClick={() => handleGameToggle(game)}
              >
                {game}
                <span className={`icon ${formData.favoriteGames.includes(game) ? 'icon-selected' : 'icon-unselected'}`}>
                  {formData.favoriteGames.includes(game) ? '‚ù§Ô∏è' : 'ü§ç'}
                </span>
              </div>
            ))}
          </div>
        </div>
      </div>
      <form onSubmit={handleSubmit} encType='multipart/form-data'>
        <div className='form-group'>
          <label>Update Bio:</label>
          <textarea
            name='bio'
            value={formData.bio}
            onChange={(e) => setFormData({ ...formData, bio: e.target.value })}
            rows='4'
          />
        </div>
        <div className='form-group'>
          <label>Update Profile Picture:</label>
          <input
            type='file'
            name='profilePicture'
            onChange={(e) => setFormData({ ...formData, profilePicture: e.target.files[0] })}
          />
        </div>
        <button type='submit' className='btn save-btn'>Save Changes</button>
      </form>
    </div>
  );
};

export default Profile;
